import React from 'react'
import axios from 'axios'
import { Button, Container, Dimmer, Icon, Image, Item, Label, Loader, Message, Segment } from 'semantic-ui-react'
import { ProductListURL, addToCartURL } from '../constants'
import { authAxios } from '../utils'


class ProductList extends React.Component {
    state = {
        loading: false,
        error: null,
        data: []
    }

    componentDidMount() {
        this.setState({ loading: true })
        axios.get(ProductListURL)
            .then(res => {
                console.log(res.data)
                this.setState({ data: res.data, loading: false })
            })
            .catch(err => {
                this.setState({ error: err, loading: false })
            })

    }

    handleAddToCart = slug => {
        this.setState({ loading: true })
        authAxios
            .post(addToCartURL, { slug })
            .then(res => {
                console.log(res.data)
                // update cart count
                this.setState({ loading: false })
            })
            .catch(err => {
                this.setState({ error: err, loading: false })
            })
    }

    render() {
        const { data, error, loading } = this.state
        return (
            <Container>
                {error && (
                    <Message
                        error
                        header='There were some errors with your submission'
                        content={JSON.stringify(error)}
                    />
                )}
                {loading && (
                    <Segment>
                        <Dimmer active inverted>
                            <Loader inverted>Loading</Loader>
                        </Dimmer>

                        <Image src='/images/wireframe/short-paragraph.png' />
                    </Segment>
                )}

                <Item.Group divided>
                    {data.map(item => {
                        return (<Item key={item.id}>
                            <Item.Image src={item.image} />
                            <Item.Content>
                                <Item.Header as='a'>{item.title}</Item.Header>
                                <Item.Meta>
                                    <span className='cinema'>{item.category}</span>
                                </Item.Meta>
                                <Item.Description>{item.description}</Item.Description>
                                <Item.Extra>
                                    <Button primary floated='right' icon labelPosition='right' onClick={() => this.handleAddToCart(item.slug)}>
                                        Add to cart
                                    <Icon name='plus cart' />
                                    </Button>
                                    {item.discount_price && (<Label color=
                                        {item.label === 'primary' ? "blue" : item.label === 'secondary' ?
                                            "green" : "olive"} >{item.label}</Label>)}
                                </Item.Extra>
                            </Item.Content>
                        </Item>)
                    })}
                </Item.Group>
            </Container>
        );
    }
}

export default ProductList
